git log --all --no-merges --shortstat --pretty=format:'commit_hash %H commit_hash_abbreviated %h tree_hash %T tree_hash_abbreviated %t parent_hashes %P parent_hashes_abbreviated %p author_name %an author_email %ae author_date %ad author_date_rfc2822_style %aD author_date_relative %ar author_date_unix_timestamp %at author_date_iso_8601 %ai committer_name %cn committer_email %ce committer_date %cd committer_date_relative %cr subject %s'| paste -d " " - - - > gitlog.json